mapper.insert:
insert into standard_suite(
	standard_id,
	suite_id,
	createtime
)
values(
	#{standard_id},
	#{suite_id},
	#{createtime}
)

mapper.select:
select a.id as suitId, a.suit_name as suitName, a.skill_type as skillType, b.standard_name as standardName, b.standard_number as standardNumber, b.updatetime as uploadDate from suite a inner join standard_suite c on a.id=c.suite_id inner join teststandard b on c.standard_id=b.standard_id where 1=1
and a.test_type = #{testType}
and a.skill_type = #{skillType}
and a.createtime > #{uploadStartDate}
and a.createtime < #{uploadStartDate}
and a.suit_name = '%${keyWord}%'

select suit_name as suitName,test_type as testType,skill_type as skillType 
from suite 
where id=#{sutiId}

select id as suitId,suit_name as suitName,test_type as testType,skill_type as skillType 
from suite 
where id=#{sutiId}
