mapper.select:
slect a.name b.count(*) from user a left join testinfo b where a.user_id=b.user_id and 1=1
and starttime like '%${year}-${month}%'
and starttime > #{startDate} and starttime < #{endDate}
and starttime like '%${year}%'
and test_mode=#{testMode}

select test_id as testId,test_name as testName,test_type as
testType,applicant,starttime,test_state as testState,count(*) as total
from testinfo
where 1=1

	and test_type=#{testType}


	and test_state=#{testState}


	and starttime > #{appStartDate}


	and starttime < #{appEndDate}


	and endtime > #{testMode}


	and endtime < #{testMode}


	and test_name like '%${keyWord}%'

select t.test_id testId, t.test_name testName, t.test_type testType, t.endtime successDate   from testinfo t 
left join user u on t.user_id=u.user_id


	and u.user_id=#{userId}


	and t.test_type=#{testType}


	and t.starttime > #{startDate}


	and t.endtime < #{endDate}


	and t.test_name like '%${keyWord}%'


	and t.test_mode=#{testMode}
