mapper.select:
        select count(*) from testinfo where 1=1

			and starttime like '%${year}-${month}%'


			and starttime > #{startDate} and starttime < #{endDate}


			and starttime like '%${year}%'


			and test_mode=#{testMode}

slect a.name b.count(*) from user a left join testinfo b
where a.user_id=b.user_id and 1=1 

	and starttime like '%${year}-${month}%'


	and starttime > #{startDate} and starttime < #{endDate}


	and starttime like '%${year}%'


	and test_mode=#{testMode}
